import { type } from '../utility';
export const ActionTypes = {
  DO_LOGOUT: type('[Common] Do Logout'),
  DO_LOGOUT_SUCCESS: type('[Common] Do Logout Success'),
  DO_LOGOUT_FAIL: type('[Common] Do Logout Fail'),
  DO_CLEAR: type('[Common] Do Clear'),
  DO_CLEAR_SUCCESS: type('[Common] Do Clear Success'),
  DO_CLEAR_FAIL: type('[Common] Do Clear Fail')
}; // LOGOUT

export class DoLogoutAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_LOGOUT;
  }

}
export class DoLogoutSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_LOGOUT_SUCCESS;
  }

}
export class DoLogoutFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_LOGOUT_FAIL;
  }

}
export class DoClearAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_CLEAR;
  }

}
export class DoClearSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_CLEAR_SUCCESS;
  }

}
export class DoClearFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_CLEAR_FAIL;
  }

}
