import { type } from '../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  GET_SALES_COUNT: type('[SalesLayout] Get Sales Counts'),
  GET_SALES_COUNT_SUCCESS: type('[SalesLayout] Get Sales Counts Success'),
  GET_SALES_COUNT_FAIL: type('[SalesLayout] Get Sales Counts Fail')
};
/* Get Sales Counts*/

export class GetSalesCountAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.GET_SALES_COUNT;
  }

}
export class GetSalesCountSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.GET_SALES_COUNT_SUCCESS;
  }

}
export class GetSalesCountFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.GET_SALES_COUNT_FAIL;
  }

}
