import { type } from '../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  GET_CATALOG_COUNT: type('[CatalogLayout] Get Catalog Count'),
  GET_CATALOG_COUNT_SUCCESS: type('[CatalogLayout] Get Catalog Count Success'),
  GET_CATALOG_COUNT_FAIL: type('[CatalogLayout] Get Catalog Count Success Fail')
};
/* Get Catalog Count Actions */

export class GetCatalogCountAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.GET_CATALOG_COUNT;
  }

}
export class GetCatalogCountSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.GET_CATALOG_COUNT_SUCCESS;
  }

}
export class GetCatalogCountFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.GET_CATALOG_COUNT_FAIL;
  }

}
