import { type } from '../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  GET_CUSTOMER_COUNT: type('[CustomerLayout] Get Customer Count'),
  GET_CUSTOMER_COUNT_SUCCESS: type('[CustomerLayout] Get Customer Count Success'),
  GET_CUSTOMER_COUNT_FAIL: type('[CustomerLayout] Get Customer Count Fail')
};
/* Get Customer Count Actions */

export class GetCustomerCountAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.GET_CUSTOMER_COUNT;
  }

}
export class GetCustomerCountSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.GET_CUSTOMER_COUNT_SUCCESS;
  }

}
export class GetCustomerCountFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.GET_CUSTOMER_COUNT_FAIL;
  }

}
