import { type } from '../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  DO_NEW_GENERAL_SETTINGS: type('[Settings] Do New general setting'),
  DO_NEW_GENERAL_SETTINGS_SUCCESS: type('[Settings] Do New general setting Success'),
  DO_NEW_GENERAL_SETTINGS_FAIL: type('[Settings] Do New general setting Fail'),
  DO_GET_GENERAL_SETTINGS: type('[Settings] Do get general setting'),
  DO_GET_GENERAL_SETTINGS_SUCCESS: type('[Settings] Do get general setting Success'),
  DO_GET_GENERAL_SETTINGS_FAIL: type('[Settings] Do get general setting Fail'),
  MAINTENANCE_MODE: type('[Settings] maintenance mode'),
  MAINTENANCE_MODE_SUCCESS: type('[Settings] maintenance mode Success'),
  MAINTENANCE_MODE_FAIL: type('[Settings] maintenance mode Fail')
}; // NEW GENERAL SETTINGS

export class DoNewGeneralSettingAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_GENERAL_SETTINGS;
  }

}
export class DoNewGeneralSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_GENERAL_SETTINGS_SUCCESS;
  }

}
export class DoNewGeneralSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_GENERAL_SETTINGS_FAIL;
  }

} // GET GENERAL SETTINGS

export class DoGetGeneralSettingAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_GET_GENERAL_SETTINGS;
  }

}
export class DoGetGeneralSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_GET_GENERAL_SETTINGS_SUCCESS;
  }

}
export class DoGetGeneralSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_GET_GENERAL_SETTINGS_FAIL;
  }

} // MAINTENANCE MODE

export class MaintenanceModeAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.MAINTENANCE_MODE;
  }

}
export class MaintenanceModeSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.MAINTENANCE_MODE_SUCCESS;
  }

}
export class MaintenanceModeFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.MAINTENANCE_MODE_FAIL;
  }

}
