import { type } from '../../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  DO_NEW_SEO_SITE_SETTINGS: type('[Settings] Do New  Seo Site setting'),
  DO_NEW_SEO_SITE_SETTINGS_SUCCESS: type('[Settings] Do New  SeoSite setting Success'),
  DO_NEW_SEO_SITE_SETTINGS_FAIL: type('[Settings] Do New  Seo Site setting Fail'),
  DO_SEO_SITE_SETTINGS: type('[Settings] Do get Seo Site setting'),
  DO_SEO_SITE_SETTINGS_SUCCESS: type('[Settings] Do get Seo Site setting Success'),
  DO_SEO_SITE_SETTINGS_FAIL: type('[Settings] Do get  Seo Site setting Fail')
}; // NEW SEO SITE SETTINGS

export class DoNewSeoSiteSettingAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_SEO_SITE_SETTINGS;
  }

}
export class DoNewSeoSiteSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_SEO_SITE_SETTINGS_SUCCESS;
  }

}
export class DoNewSeoSiteSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_SEO_SITE_SETTINGS_FAIL;
  }

} // GET  SEO SITE SETTINGS

export class DoGetSeoSiteSettingAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_SEO_SITE_SETTINGS;
  }

}
export class DoGetSeoSiteSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_SEO_SITE_SETTINGS_SUCCESS;
  }

}
export class DoGetSeoSiteSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_SEO_SITE_SETTINGS_FAIL;
  }

}
