import { type } from '../../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  DO_NEW_SOCIAL: type('[Settings social] Do New social'),
  DO_NEW_SOCIAL_SUCCESS: type('[Settings social] Do New social Success'),
  DO_NEW_SOCIAL_FAIL: type('[Settings social] Do New social Fail'),
  DO_GET_SOCIAL: type('[Settings social] Do get social'),
  DO_GET_SOCIAL_SUCCESS: type('[Settings social] Do get social Success'),
  DO_GET_SOCIAL_FAIL: type('[Settings social] Do get social Fail')
}; // NEW SOCIAL SETTINGS

export class DoNewSocialAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_SOCIAL;
  }

}
export class DoNewSocialSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_SOCIAL_SUCCESS;
  }

}
export class DoNewSocialFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_SOCIAL_FAIL;
  }

} // GET SOCIAL SETTINGS

export class DoGetSocialAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_GET_SOCIAL;
  }

}
export class DoGetSocialSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_GET_SOCIAL_SUCCESS;
  }

}
export class DoGetSocialFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_GET_SOCIAL_FAIL;
  }

}
