import { type } from '../../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  DO_NEW_ORDER_SETTINGS: type('[Settings] Do New  Order setting'),
  DO_NEW_ORDER_SETTINGS_SUCCESS: type('[Settings] Do New  Order setting Success'),
  DO_NEW_ORDER_SETTINGS_FAIL: type('[Settings] Do New  Order setting Fail'),
  DO_ORDER_SETTINGS: type('[Settings] Do get Order setting'),
  DO_ORDER_SETTINGS_SUCCESS: type('[Settings] Do get Order setting Success'),
  DO_ORDER_SETTINGS_FAIL: type('[Settings] Do get  Order setting Fail')
}; // NEW SEO SITE SETTINGS

export class DoNewOrderSettingAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_ORDER_SETTINGS;
  }

}
export class DoNewOrderSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_ORDER_SETTINGS_SUCCESS;
  }

}
export class DoNewOrderSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_ORDER_SETTINGS_FAIL;
  }

} // GET  SEO SITE SETTINGS

export class DoGetOrderSettingAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_ORDER_SETTINGS;
  }

}
export class DoGetOrderSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_ORDER_SETTINGS_SUCCESS;
  }

}
export class DoGetOrderSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_ORDER_SETTINGS_FAIL;
  }

}
