import { type } from '../../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  DO_NEW_PRODUCT_SETTINGS: type('[Settings] Do New  Product setting'),
  DO_NEW_PRODUCT_SETTINGS_SUCCESS: type('[Settings] Do New  Product setting Success'),
  DO_NEW_PRODUCT_SETTINGS_FAIL: type('[Settings] Do New  Product setting Fail'),
  DO_PRODUCT_SETTINGS: type('[Settings] Do get Product setting'),
  DO_PRODUCT_SETTINGS_SUCCESS: type('[Settings] Do get Product setting Success'),
  DO_PRODUCT_SETTINGS_FAIL: type('[Settings] Do get  Product setting Fail')
}; // NEW SEO SITE SETTINGS

export class DoNewProductSettingAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_PRODUCT_SETTINGS;
  }

}
export class DoNewProductSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_PRODUCT_SETTINGS_SUCCESS;
  }

}
export class DoNewProductSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_NEW_PRODUCT_SETTINGS_FAIL;
  }

} // GET  SEO SITE SETTINGS

export class DoGetProductSettingAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_PRODUCT_SETTINGS;
  }

}
export class DoGetProductSettingSuccessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_PRODUCT_SETTINGS_SUCCESS;
  }

}
export class DoGetProductSettingFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_PRODUCT_SETTINGS_FAIL;
  }

}
