// action
import * as actions from '../action/audit-log.action';
import { AuditLogStateRecord } from './audit-log.state'; // Model

export const initialState = new AuditLogStateRecord();
export function reducer(state = initialState, {
  type,
  payload
}) {
  if (!type) {
    return state;
  }

  switch (type) {
    case actions.ActionTypes.AUDIT_LOG_LIST:
      {
        return Object.assign({}, state, {
          auditLogList: {},
          auditLogListLoading: true,
          auditLogListLoaded: false,
          auditLogListFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_LOG_LIST_SUCCESS:
      {
        return Object.assign({}, state, {
          auditLogList: payload.data,
          auditLogListLoading: false,
          auditLogListLoaded: true,
          auditLogListFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_LOG_LIST_FAIL:
      {
        return Object.assign({}, state, {
          auditLogList: {},
          auditLogListLoading: false,
          auditLogListLoaded: true,
          auditLogListFailed: true
        });
      }

    case actions.ActionTypes.AUDIT_LOG_DELETE:
      {
        return Object.assign({}, state, {
          auditLogDelete: {},
          auditLogDeleteLoading: true,
          auditLogDeleteLoaded: false,
          auditLogDeleteFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_LOG_DELETE_SUCCESS:
      {
        return Object.assign({}, state, {
          auditLogDelete: payload.data,
          auditLogDeleteLoading: false,
          auditLogDeleteLoaded: true,
          auditLogDeleteFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_LOG_DELETE_FAIL:
      {
        return Object.assign({}, state, {
          auditLogDelete: {},
          auditLogDeleteLoading: false,
          auditLogDeleteLoaded: true,
          auditLogDeleteFailed: true
        });
      }

    case actions.ActionTypes.AUDIT_LOG_LIST:
      {
        return Object.assign({}, state, {
          auditLogListCount: 0,
          auditLogListCountLoading: true,
          auditLogListCountLoaded: false,
          auditLogListCountFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_LOG_LIST_COUNT_SUCCESS:
      {
        return Object.assign({}, state, {
          auditLogListCount: payload.data,
          auditLogListCountLoading: false,
          auditLogListCountLoaded: true,
          auditLogListCountFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_LOG_LIST_FAIL:
      {
        return Object.assign({}, state, {
          auditLogListCount: 0,
          auditLogListCountLoading: false,
          auditLogListCountLoaded: true,
          auditLogListCountFailed: true
        });
      }

    case actions.ActionTypes.DELETE_LOGS:
      {
        return Object.assign({}, state, {
          DeleteLogs: 0,
          DeleteLogsLoading: true,
          DeleteLogsLoaded: false,
          DeleteLogsFailed: false
        });
      }

    case actions.ActionTypes.DELETE_LOGS_SUCCESS:
      {
        return Object.assign({}, state, {
          DeleteLogs: payload,
          DeleteLogsLoading: false,
          DeleteLogsLoaded: true,
          DeleteLogsFailed: false
        });
      }

    case actions.ActionTypes.DELETE_LOGS_FAIL:
      {
        return Object.assign({}, state, {
          DeleteLogs: 0,
          DeleteLogsLoading: false,
          DeleteLogsLoaded: true,
          DeleteLogsFailed: true
        });
      }

    case actions.ActionTypes.AUDIT_MODULE_LIST:
      {
        return Object.assign({}, state, {
          auditLogModuleLoading: false,
          auditLogModuleLoaded: true,
          auditLogModuleFailed: true
        });
      }

    case actions.ActionTypes.AUDIT_MODULE_LIST_SUCCESS:
      {
        return Object.assign({}, state, {
          auditLogModule: payload.data,
          auditLogListLoading: false,
          auditLogListLoaded: true,
          auditLogListFailed: false
        });
      }

    case actions.ActionTypes.AUDIT_MODULE_LIST_FAIL:
      {
        return Object.assign({}, state, {
          auditLogModuleLoading: false,
          auditLogModuleLoaded: true,
          auditLogModuleFailed: true
        });
      }

    case actions.ActionTypes.GET_USER_LIST:
      {
        return Object.assign({}, state, {
          listLoading: true,
          listLoaded: false,
          listFailed: false
        });
      }

    case actions.ActionTypes.GET_USER_LIST_SUCCESS:
      {
        return Object.assign({}, state, {
          userList: payload.data,
          listLoading: false,
          listLoaded: true,
          listFailed: false
        });
      }

    case actions.ActionTypes.GET_USER_LIST_FAIL:
      {
        return Object.assign({}, state, {
          listLoading: false,
          listLoaded: true,
          listFailed: true
        });
      }

    default:
      {
        return state;
      }
  }
}
export const auditLogList = state => state.auditLogList;
export const auditLogListLoading = state => state.auditLogListLoading;
export const auditLogListLoaded = state => state.auditLogListLoaded;
export const auditLogListFailed = state => state.auditLogListFailed;
export const auditLogDelete = state => state.auditLogDelete;
export const auditLogDeleteLoading = state => state.auditLogDeleteLoading;
export const auditLogDeleteLoaded = state => state.auditLogDeleteLoaded;
export const auditLogDeleteFailed = state => state.auditLogDeleteFailed;
export const auditLogListCount = state => state.auditLogListCount;
export const auditLogListCountLoading = state => state.auditLogListCountLoading;
export const auditLogListCountLoaded = state => state.auditLogListCountLoaded;
export const auditLogListCountFailed = state => state.auditLogListCountFailed;
export const auditLogModule = state => state.auditLogModule;
export const auditLogModuleLoading = state => state.auditLogModuleLoading;
export const auditLogModuleLoaded = state => state.auditLogModuleLoaded;
export const auditLogModuleFailed = state => state.auditLogModuleFailed;
export const getUserListLoading = state => state.listLoading;
export const getUserListLoaded = state => state.listLoaded;
export const getUserListFailed = state => state.listFailed;
export const getUserList = state => state.userList;
export const deleteLogsLoading = state => state.deleteLogsLoading;
export const deleteLogsLoaded = state => state.deleteLogsLoaded;
export const deleteLogsFailed = state => state.deleteLogsFailed;
export const deleteLogs = state => state.DeleteLogs;
