import * as actions from '../action/layout.action';
import { SalesLayoutStateRecord } from './layout.state';
export const initialState = new SalesLayoutStateRecord();
export function reducer(state = initialState, {
  type,
  payload
}) {
  if (!type) {
    return state;
  }

  switch (type) {
    /* get sales count */
    case actions.ActionTypes.GET_SALES_COUNT:
      {
        return Object.assign({}, state, {
          salesCount: {},
          salesCountLoading: true,
          salesCountLoaded: false,
          salesCountFailed: false
        });
      }

    case actions.ActionTypes.GET_SALES_COUNT_SUCCESS:
      {
        return Object.assign({}, state, {
          salesCount: payload.data,
          salesCountLoading: false,
          salesCountLoaded: true,
          salesCountFailed: false
        });
      }

    case actions.ActionTypes.GET_SALES_COUNT_FAIL:
      {
        return Object.assign({}, state, {
          salesCount: {},
          salesCountLoading: false,
          salesCountLoaded: false,
          salesCountFailed: true
        });
      }

    default:
      {
        return state;
      }
  }
}
export const salesCount = state => state.salesCount;
export const salesCountLoading = state => state.salesCountLoading;
export const salesCountLoaded = state => state.salesCountLoaded;
export const salesCountFailed = state => state.salesCountFailed;
