import { type } from '../../../shared/utility/utilityHelpers';
export const ActionTypes = {
  DO_CHANGE_PASWORD: type('[change] Do Change Password'),
  DO_CHANGE_PASWORD_SUCCESS: type('[change] Do Change Password Success'),
  DO_CHANGE_PASWORD_FAIL: type('[change] Do Change Password Fail')
}; // CHANGE PASSWORD

export class DoChangePasswordAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_CHANGE_PASWORD;
  }

}
export class DoChangePasswordSucessAction {
  constructor(payload) {
    this.payload = payload;
    this.type = ActionTypes.DO_CHANGE_PASWORD_SUCCESS;
  }

}
export class DoChangePasswordFailAction {
  constructor(payload = null) {
    this.payload = payload;
    this.type = ActionTypes.DO_CHANGE_PASWORD_FAIL;
  }

}
