import * as actions from '../role-action/role.action';
import { RoleStateRecord } from '../role-reducer/role.state';
import { RoleListResponseModel } from '../role.models/role-list-response.model';
export const initialState = new RoleStateRecord();
export function reducer(state = initialState, {
  type,
  payload
}) {
  if (!type) {
    return state;
  }

  switch (type) {
    case actions.ActionTypes.DO_ROLE_LIST:
      {
        return Object.assign({}, state, {
          listLoading: true,
          listLoaded: false,
          listFailed: false
        });
      }

    case actions.ActionTypes.DO_NEW_ROLE:
      {
        return Object.assign({}, state, {
          addLoading: true,
          addLoaded: false,
          addFailed: false
        });
      }

    case actions.ActionTypes.DO_UPDATE_ROLE:
      {
        return Object.assign({}, state, {
          updateLoading: true,
          updateLoaded: false,
          updateFailed: false
        });
      }

    case actions.ActionTypes.GET_ROLE_COUNT:
      {
        return Object.assign({}, state, {
          countLoading: true,
          countLoaded: false,
          countFailed: false
        });
      }

    case actions.ActionTypes.GET_ROLE_COUNT_SUCCESS:
      {
        return Object.assign({}, state, {
          countLoading: false,
          countLoaded: true,
          countFailed: false,
          roleCount: payload.data
        });
      }

    case actions.ActionTypes.DO_ROLE_LIST_SUCCESS:
      {
        const rolelistModel = payload.data.map(_roles => {
          const temproleModel = new RoleListResponseModel(_roles);
          return temproleModel;
        });
        return Object.assign({}, state, {
          listLoading: false,
          listLoaded: true,
          listFailed: false,
          rolelist: rolelistModel
        });
      }

    case actions.ActionTypes.DO_NEW_ROLE_SUCCESS:
      {
        return Object.assign({}, state, {
          addLoading: false,
          addLoaded: true,
          addFailed: false,
          newRoleStatus: payload
        });
      }

    case actions.ActionTypes.DO_NEW_ROLE_FAIL:
      {
        return Object.assign({}, state, {
          addLoading: false,
          addLoaded: false,
          addFailed: true
        });
      }

    case actions.ActionTypes.DO_UPDATE_ROLE_SUCCESS:
      {
        return Object.assign({}, state, {
          updateLoading: false,
          updateLoaded: true,
          updateFailed: false,
          updaterole: payload
        });
      }

    case actions.ActionTypes.DO_ROLE_LIST_FAIL:
      return Object.assign({}, state, {
        listLoading: false,
        listLoaded: false,
        listFailed: true
      });

    case actions.ActionTypes.DO_UPDATE_ROLE_FAIL:
      {
        return Object.assign({}, state, {
          updateLoading: false,
          updateLoaded: false,
          updateFailed: true
        });
      }

    case actions.ActionTypes.GET_ROLE_COUNT_FAIL:
      {
        return Object.assign({}, state, {
          countLoading: false,
          countLoaded: true,
          countFailed: true
        });
      }

    case actions.ActionTypes.DELETA_ROLE:
      {
        return Object.assign({}, state, {});
      }

    case actions.ActionTypes.DELETA_ROLE_SUCCESS:
      {
        return Object.assign({}, state, {
          roleDelete: payload
        });
      }

    case actions.ActionTypes.DELETA_ROLE_FAIL:
      {
        return Object.assign({}, state, {});
      }

    default:
      {
        return state;
      }
  }
}
export const getRoleList = state => state.rolelist;
export const getNewRoleStatus = state => state.newRoleStatus;
export const getUpdateRole = state => state.updaterole;
export const getRoleCount = state => state.roleCount;
export const getpaginatioListLoading = state => state.listLoading;
export const getpaginatioListLoaded = state => state.listLoaded;
export const getpaginatioListFailed = state => state.listFailed;
export const getpaginatioCountLoading = state => state.countLoading;
export const getpaginatioCountLoaded = state => state.countLoaded;
export const getpaginatioCountFailed = state => state.countFailed;
export const getpaginatioAddLoading = state => state.addLoading;
export const getpaginatioAddLoaded = state => state.addLoaded;
export const getpaginatioAddFailed = state => state.addFailed;
export const getpaginatioUpdateLoading = state => state.updateLoading;
export const getpaginatioUpdateLoaded = state => state.updateLoaded;
export const getpaginatioUpdateFailed = state => state.updateFailed;
export const getRoleDelete = state => state.roleDelete;
